function [Vp,Vs,Ip,PR,RHOB,Phi,a, KsV, GsV, KDry, GDry, Ks, Gs]=SoftSandNew(Kf,RHOf,Phi,Quartz,Clay,Feldspar,Limestone,Dolomite,Pressure,PhiC,Coordination,Fudge,varargin)%Uncemented sand model (implemented in Petrotools)%				INPUTS%Kf				Fluid bulk modulus%RHOB			Fluid density%Phi			Porosity (fraction)%Quartz, etc.	Volume mineral content in solid phase (fraction)%Pressure		Effective pressure (MPa)%PhiC			Critical porosity ~0.4%Coordination	Coordination number ~6%Fudge			Reduction in shear stiffness -- 1 by default%a is the contact radius between two spheres at input pressure %Written by Jack Dvorkin 2/03%Balancing mineralogyDolomite=1-(Quartz+Clay+Feldspar+Limestone);%Constants for clay%RHOClay=2.53; KClay=21; GClay=7;%RHOClay=2.53; KClay=25; GClay=16;if ~isempty(varargin{1})            if length(varargin)==1        param = varargin{1};    else        param = varargin;    end    dum = strcmp('c',param) | strcmp('C',param) | strcmp('clay',param) | strcmp('Clay',param);        indx = find(dum==1,1);        KClay=param{indx+1}{1}; GClay=param{indx+1}{2}; RHOClay=param{indx+1}{3};else    RHOClay=2.65; KClay=21; GClay=7;endRHOClay=2.65; KClay=21; GClay=7;%Solid-phase elastic moduliKsV=Quartz*36.6+Clay*KClay+Limestone*76.8+Dolomite*94.9+Feldspar.*75.6;KsR=1./(Quartz/36.6+Clay/KClay+Limestone/76.8+Dolomite/94.9+Feldspar./75.6);Ks=0.5*(KsV+KsR);GsV=Quartz*45+Clay*GClay+Limestone*32+Dolomite*45+Feldspar.*25.6;GsR=1./(Quartz/45+Clay/GClay+Limestone/32+Dolomite/45+Feldspar./25.6);Gs=0.5*(GsV+GsR);Ms=Ks+(4/3)*Gs;NUs=0.5*(Ms./Gs-2)./(Ms./Gs-1);%Solid-phase densityRHOs=Quartz*2.65+Clay*RHOClay+Limestone*2.71+Dolomite*2.87+Feldspar.*2.63;%RHOs=Quartz*2.65+Clay*2.65+Limestone*2.71+Dolomite*2.87+Feldspar.*2.63;%ILLITE%RHOs=2.58; Ms=RHOs.*4.32.*4.32; Gs=RHOs.*2.54.*2.54; NUs=0.5*(Ms./Gs-2)./(Ms./Gs-1); Ks=Ms-(4/3)*Gs;P=Pressure./1000;C=Coordination;% ================== Dry Rock ====================%Effective K and G at PhiC%Hertz contact radius (R=1)a=((3.*3.14.*(1-NUs)./(2.*C.*(1-PhiC).*Gs)).*P).^(1./3);%Normal contact stiffnessSN=4.*a.*Gs./(1-NUs);%Shear contact stiffnessST=8.*a.*Gs./(2-NUs);%Shear contact stiffness FUDGED (reduced)ST=ST./Fudge;%Bulk modulusKhat=C.*(1.-PhiC).*SN./(12.*3.14);%Shear modulusGhat=C.*(1.-PhiC).*(SN+1.5.*ST)./(20.*3.14);%Effective bulk and shear moduli at porosity Phi<=PhiCKDry1=1./((Phi./PhiC)./(Khat+4.*Ghat./3)+((PhiC-Phi)./PhiC)./(Ks+4.*Ghat./3))-4.*Ghat./3;ZZ1=(Ghat./6).*(9.*Khat+8.*Ghat)./(Khat+2.*Ghat);GDry1=1./((Phi./PhiC)./(Ghat+ZZ1)+((PhiC-Phi)./PhiC)./(Gs+ZZ1))-ZZ1; MDry1 = KDry1+(4./3).*GDry1;NuDry1=0.5.*(MDry1./GDry1-2)./(MDry1./GDry1-1);%Effective bulk and shear moduli at porosity Phi>PhiCKDry2 = 1./(((1-Phi)./(1-PhiC))./(Khat+4.*Ghat./3)+((Phi-PhiC)./(1-PhiC))./(4.*Ghat./3))-4.*Ghat./3;ZZ2 = (Ghat./6).*(9.*Khat+8.*Ghat)./(Khat+2.*Ghat);GDry2 = 1./(((1-Phi)./(1-PhiC))./(Ghat+ZZ2)+((Phi-PhiC)./(1-PhiC))./(ZZ2))-ZZ2;MDry2 = KDry2+(4./3).*GDry2;NuDry2=0.5.*(MDry2./GDry2-2)./(MDry2./GDry2-1);MDry=MDry1.*(Phi<=PhiC)+MDry2.*(Phi>PhiC);GDry=GDry1.*(Phi<=PhiC)+GDry2.*(Phi>PhiC);NuDry=NuDry1.*(Phi<=PhiC)+NuDry2.*(Phi>PhiC);KDry=MDry-(4/3)*GDry;% ==============================================% ================== Saturated Rock ============KSat=Ks.*(Phi.*KDry-(1+Phi).*Kf.*KDry./Ks+Kf)./((1-Phi).*Kf+Phi.*Ks-Kf.*KDry./Ks);MSat=KSat+(4/3)*GDry;RHOB=(1-Phi).*RHOs+Phi.*RHOf;Vp=sqrt(MSat./RHOB);Vs=sqrt(GDry./RHOB);Ip=Vp.*RHOB;PR=0.5*(MSat./GDry-2)./(MSat./GDry-1);